import React from 'react'
import ReactDOM from 'react-dom'
import Tree from '@naisutech/react-tree'
import PropTypes from "prop-types";

import './styles.css'

class CurriculumTreeSection extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
       isLoaded: false,
       nodesOfTree: []
    }

    this.key = props.startNodeId;
  }

  loadTree() {
    console.log(this.props.urlTreeService + "&node_id="+this.props.startNodeId)
    fetch(this.props.urlTreeService + "&node_id="+this.props.startNodeId)
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        nodesOfTree: json.nodesOfTree
      })
    })
  }


  render() {
    const {isLoaded, nodesOfTree} = this.state;

    if (isLoaded) {
      return (
            nodesOfTree
      )
    } else {
      this.loadTree()
      return 'loading'
    }
  }
}

export default CurriculumTreeSection;

CurriculumTreeSection.propTypes = {
  urlTreeService: PropTypes.string.isRequired,
  startNodeId: PropTypes.number.isRequired
};
