//function App() {
  constructor(props) {
    super(props);
    this.state = {
      url_treeservice: document.getElementById('treeservice').textContent,
      isloaded: false,
      nodes: []
    }
  }

  loadTree() {
console.log('loadtree')
    fetch(this.state.url_treeservice)
    .then(res => res.json())
    .then(json => {
      console.log(JSON.stringify(json))
      this.setState({
        isloaded: true,
        nodes: JSON.parse(json)
      })
    });
  }
