  render() {
    const {url_treeservice, isloaded, nodes} = this.state;

    if (isloaded !== true) {
      this.loadTree()
    }

    if (isloaded) {
      return (
        <div className="showtree" style={{ display: 'flex', flexWrap: 'nowrap', flexGrow: 1 }}>
          <div style={{ width: '50%', display: 'flex', flexDirection: 'column' }}>
            <Tree nodes={nodes} showEmptyItems size="half" theme={'light'}/>
          </div>

        </div>
      )
    } else {
      return <div className="showtree">
