import React, { Suspense } from 'react'
import ReactDOM from 'react-dom'


import './styles.css'
import Tree from '@naisutech/react-tree';

class CurriculumTree extends React.Component {



  constructor(props) {
    super(props)
    this.state = {
      urlTreeservice: document.getElementById('treeservice').textContent,
      nodeIdToLoad: 224,
      loadedTreeNodeIds: [],
      nodesOfTree: []
    }



    //document.getElementById('treeservice-nodeid').textContent
  }

  handleTreeSection(urlTreeservice,nodeIdToLoad){

    const CurriculumTreeSection = React.lazy(() => import('./CurriculumTreeSection'));

    return
    <div>
      <Suspense fallback={<div>Loading...</div>}>
    <CurriculumTreeSection urlTreeService={urlTreeservice} startNodeId={nodeIdToLoad} showEmptyItems size="half" theme={'light'} />
        </Suspense>
    </div>

    //Calling a function of other class (with argument)
   // return new CurriculumTreeSection({urlTreeService: this.state.urlTreeservice, startNodeId: this.state.nodeIdToLoad}).render();
  }

  loadTreeSection(urlTreeservice,nodeIdToLoad) {

 /*   const nodes = ({sections}) => (
      <Tree nodes=
              {sections.map(section => <CurriculumTreeSection urlTreeService={urlTreeservice} startNodeId={nodeIdToLoad} showEmptyItems size="half" theme={'light'} />)}
            showEmptyItems size="half" theme={'light'}/>
    );*/




  }


  render() {
    const { urlTreeservice, nodeIdToLoad, loadedTreeNodeIds, nodesOfTree } = this.state;

    if (this.checkIfLoaded(loadedTreeNodeIds, nodeIdToLoad) === true) {
      return (
        <div className="showtree" style={{ display: 'flex', flexWrap: 'nowrap', flexGrow: 1 }}>
          <div style={{ width: '50%', display: 'flex', flexDirection: 'column' }}>
            {nodesOfTree}
          </div>
        </div>
      )
    } else {

      const section = this.handleTreeSection(urlTreeservice,nodeIdToLoad)

        const currentLoadedTreeNodeIds = this.state.loadedTreeNodeIds
        currentLoadedTreeNodeIds.push({ value: nodeIdToLoad });

        const currentNodesOfTree = this.state.nodesOfTree
        currentNodesOfTree.push({ value: section });
        // Set state
        this.setState({ currentLoadedTreeNodeIds, currentNodesOfTree });
        //  console.log(JSON.stringify(this.state))
        return ("loading")

    }
  }

  checkIfLoaded(loadedTreeNodeIds, NodeId) {
    return NodeId in loadedTreeNodeIds
  }

}

export default CurriculumTree;
CurriculumTree.propTypes = {}
