import React from 'react'

import './styles.css'
import Tree from '@naisutech/react-tree';

class CurriculumTree extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
      urlTreeService: document.getElementById('treeservice').textContent,
      nodeIdToLoad: document.getElementById('treeservice-nodeid').textContent,
      loadedTreeNodeIds: [],
      nodesOfTree: []
    }
  }

  loadTreeSection(nodeIdToLoad,
                  loadedTreeNodeIds,
                  nodesOfTree) {
    console.log(this.state.urlTreeService + "&node_id=" + nodeIdToLoad);
    fetch(this.state.urlTreeService + "&node_id=" + nodeIdToLoad)
    .then(res => res.json())
    .then(json => {
      this.setState({
        loadedTreeNodeIds: [ ...loadedTreeNodeIds, nodeIdToLoad],
          nodesOfTree: json
      })

    })
  }

  onSelect = (selectedNode) => {
      this.setState({nodeIdToLoad:selectedNode.NODEID})
  }


  render() {
    const { urlTreeService, nodeIdToLoad, loadedTreeNodeIds, nodesOfTree } = this.state;
    console.log(loadedTreeNodeIds)
    console.log(nodeIdToLoad)
    if (this.checkIfLoaded(loadedTreeNodeIds, nodeIdToLoad) === true) {
      console.log("HEUREKA")
      console.log(nodesOfTree);
      return (
        <div className="showtree" style={{ display: 'flex', flexWrap: 'nowrap', flexGrow: 1 }}>
          <div style={{ width: '50%', display: 'flex', flexDirection: 'column' }}>
            <Tree nodes={nodesOfTree} showEmptyItems size="half" theme={'light'} onSelect={onSelect}/>
          </div>
        </div>
      )
    } else {
      console.log("load teree section")
      this.loadTreeSection(
        nodeIdToLoad, loadedTreeNodeIds, nodesOfTree
      )
      console.log("loading")
      return "loading"
    }
  }



  checkIfLoaded(loadedTreeNodeIds, nodeIdToLoad) {
    return loadedTreeNodeIds.indexOf(nodeIdToLoad) >= 0
  }

}

export default CurriculumTree;
CurriculumTree.propTypes = {}
