import React from 'react'
import ReactDOM from 'react-dom'
import CurriculumTreeSection from './CurriculumTreeSection'

import './styles.css'
import Tree from '@naisutech/react-tree';

class CurriculumTree extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
      urlTreeservice: document.getElementById('treeservice').textContent,
      nodeIdToLoad: 224,
      loadedTreeNodeIds: [],
      nodesOfTree: []
    }
  }

  renderTreeSection(urlTreeservice, nodeIdToLoad) {
    const section = (
    <CurriculumTreeSection urlTreeService={urlTreeservice} startNodeId={nodeIdToLoad} />)


   // console.log(JSON.stringify(section))
    const currentLoadedTreeNodeIds = this.state.loadedTreeNodeIds
    currentLoadedTreeNodeIds.push( this.state.startNodeId );
    //console.log(currentLoadedTreeNodeIds)
    //console.log(JSON.stringify(this.state))

    const currentNodesOfTree = this.state.nodesOfTree
    currentNodesOfTree.push(section);
    // Set state
    this.setState({ currentLoadedTreeNodeIds, currentNodesOfTree });
  }


  render() {
    const { urlTreeservice, nodeIdToLoad, loadedTreeNodeIds, nodesOfTree } = this.state;

    console.log(this.checkIfLoaded(loadedTreeNodeIds, nodeIdToLoad))
    if (this.checkIfLoaded(loadedTreeNodeIds, nodeIdToLoad) === true) {
      return (
        <div className="showtree" style={{ display: 'flex', flexWrap: 'nowrap', flexGrow: 1 }}>
          <div style={{ width: '50%', display: 'flex', flexDirection: 'column' }}>
            {nodesOfTree}
          </div>
        </div>
      )
    } else {

      return "loading"


    }
  }


  checkIfLoaded(loadedTreeNodeIds, NodeId) {
    this.renderTreeSection(urlTreeservice, nodeIdToLoad)

    return loadedTreeNodeIds.includes(NodeId)
  }

}

export default CurriculumTree;
CurriculumTree.propTypes = {}
