import React from 'react'
import ReactDOM from 'react-dom'
import CurriculumTreeSection from './CurriculumTreeSection'

import './styles.css'

class CurriculumTree extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      urlTreeservice: document.getElementById('treeservice').textContent,
      nodeIdToLoad: document.getElementById('treeservice-nodeid').textContent,
      loadedScections: { }
    }
  }


  render() {
    const {urlTreeservice, nodeIdToLoad, loadedScections} = this.state;

    if (this.checkIfLoaded(loadedScections, nodeIdToLoad) === true) {
      return (
        <div className="showtree" style={{ display: 'flex', flexWrap: 'nowrap', flexGrow: 1 }}>
          <div style={{ width: '50%', display: 'flex', flexDirection: 'column' }}>
            <CurriculumTreeSection urlTreeService={urlTreeservice} startNodeId={nodeIdToLoad} showEmptyItems size="half" theme={'light'} />
          </div>
        </div>
      )
    } else {
      const sectionToAdd = <CurriculumTreeSection urlTreeService={urlTreeservice} startNodeId={nodeIdToLoad} showEmptyItems size="half" theme={'light'} />

      //return sectionToAdd

      //console.log(JSON.toLocaleString(sectionToAdd))

      const newPair = { [nodeIdToLoad]: Array.from(Array(parseInt(sectionToAdd)).keys()) };

      this.setState(
        {
          loadedScections: { ...loadedScections, ...newPair }
        });

    //  console.log(JSON.stringify(this.state))
      return ("loading")
    }
  }

  checkIfLoaded(loadedScections, NodeId) {
    return NodeId in loadedScections
  }

}

export default CurriculumTree;
CurriculumTree.propTypes = {}
