import React from 'react'
import ReactDOM from 'react-dom'
import CurriculumTreeSection from './CurriculumTreeSection'

import './styles.css'
import Tree from '@naisutech/react-tree';

class CurriculumTree extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
      urlTreeservice: document.getElementById('treeservice').textContent,
      nodeIdToLoad: 2936,
      loadedTreeNodeIds: [],
      nodesOfTree: []
    }
  }

  loadTreeSection() {
    console.log(this.props.urlTreeService + "&node_id="+this.props.startNodeId)
    fetch(this.props.urlTreeService + "&node_id="+this.props.startNodeId)
    .then(res => res.json())
    .then(json => {
      console.log('tteeee')
      console.log(json)
      this.setState({
        isLoaded: true,
        nodesOfTreeSection: json
      })
    })
  }



  render() {
    const { urlTreeservice, nodeIdToLoad, loadedTreeNodeIds, nodesOfTree } = this.state;

    console.log(this.checkIfLoaded(loadedTreeNodeIds, nodeIdToLoad))
    if (this.checkIfLoaded(loadedTreeNodeIds, nodeIdToLoad) === true) {
      console.log("HEUREKA")
      console.log(nodesOfTree);
      return (
        <div className="showtree" style={{ display: 'flex', flexWrap: 'nowrap', flexGrow: 1 }}>
          <div style={{ width: '50%', display: 'flex', flexDirection: 'column' }}>
            {nodesOfTree}
          </div>
        </div>
      )
    } else {

      const section = (
        <CurriculumTreeSection urlTreeService={urlTreeservice} startNodeId={nodeIdToLoad} />)
      console.log(section)


      loadedTreeNodeIds.push( nodeIdToLoad );
      nodesOfTree.push(section);
      // Set state
      this.setState({ loadedTreeNodeIds:loadedTreeNodeIds, nodesOfTree:nodesOfTree });

      return "loading"
    }
  }


  checkIfLoaded(loadedTreeNodeIds, nodeIdToLoad) {
    console.log(loadedTreeNodeIds)
    console.log('tttt')
    console.log(loadedTreeNodeIds.indexOf(nodeIdToLoad))
    console.log('tttt')
    return loadedTreeNodeIds.indexOf(nodeIdToLoad) >= 0? true: false
  }

}

export default CurriculumTree;
CurriculumTree.propTypes = {}
